import React, { useEffect } from "react";

const Genre = ({ genre, setGenre, setPage, type, value, setValue }) => {
  const fetchGenre = async () => {
    const data = await fetch(
      `https://api.themoviedb.org/3/genre/${type}/list?api_key=3d820eab8fd533d2fd7e1514e86292ea&language=en-US`
    );
    const { genres } = await data.json();
    console.log(genres);
    setGenre(genres);
  };

  useEffect(() => {
    fetchGenre();
  }, []);

  const CategoryAdd = (genres) => {
    setValue([...value, genres]);
    setGenre(genre.filter((g) => g.id !== genres.id));
    setPage(1);
  };

  const CategoryRemove = (genres) => {
    setValue(value.filter((g) => g.id !== genres.id));
    setGenre([...genre, genres]);
    setPage(1);
  };

  return (
    <div className="w-full px-4 mb-6">
      <div className="flex flex-wrap">
        {value &&
          value.map(({ id, name }) => (
            <div key={id} className="m-2">
              <button
                onClick={() => CategoryRemove({ id, name })}
                className="bg-gray-800 text-white px-4 py-2 rounded hover:bg-red-600 transition duration-200"
              >
                {name}
              </button>
            </div>
          ))}

        {genre &&
          genre.map(({ id, name }) => (
            <div key={id} className="m-2">
              <button
                onClick={() => CategoryAdd({ id, name })}
                className="bg-gray-700 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-200"
              >
                {name}
              </button>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Genre;
