import React, { useState, useEffect } from "react";
import Pagination from "../Components/Pagination";
import { img_300, unavailable } from "../Components/config";
import { Link } from "react-router-dom";

const Search = () => {
  const [searchText, setSearchText] = useState("");
  const [page, setPage] = useState(1);
  const [content, setContent] = useState([]);

  const fetchSearch = async () => {
    const data = await fetch(
      `https://api.themoviedb.org/3/search/multi?api_key=3d820eab8fd533d2fd7e1514e86292ea&language=en-US&query=${searchText}&page=${page}&include_adult=false`
    );
    const { results } = await data.json();
    setContent(results);
  };

  useEffect(() => {
    fetchSearch();
  }, []);

  const handleSearch = () => {
    fetchSearch();
  };

  const handleInputChange = (e) => {
    setSearchText(e.target.value);
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-10">
      {/* Search Bar */}
      <div className="flex flex-col sm:flex-row justify-center items-center gap-4 mt-10 mb-12">
        <input
          type="text"
          placeholder="Search..."
          onChange={handleInputChange}
          className="w-full sm:w-1/2 px-4 py-2 bg-gray-800 text-white rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg transition"
        >
          <i className="fas fa-search"></i>
        </button>
      </div>

      {/* Results */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {content &&
          content.map((item) => {
            const {
              name,
              title,
              poster_path,
              first_air_date,
              release_date,
              media_type,
              id,
            } = item;
            return (
              <div
                key={id}
                className="bg-gray-900 rounded-lg shadow-lg overflow-hidden"
              >
                <img
                  src={poster_path ? `${img_300}/${poster_path}` : unavailable}
                  alt={title || name}
                  className="w-full h-80 object-cover"
                />
                <div className="p-4 text-white">
                  <h5 className="text-lg font-semibold text-center mb-2">
                    {title || name}
                  </h5>
                  <div className="flex justify-between text-sm text-gray-300">
                    <span>{media_type === "tv" ? "TV" : "Movie"}</span>
                    <span>{first_air_date || release_date}</span>
                  </div>
                </div>
              </div>
            );
          })}
      </div>

      {/* Pagination */}
      {page > 1 && (
        <div className="mt-10">
          <Pagination page={page} setPage={setPage} />
        </div>
      )}
    </div>
  );
};

export default Search;
