    import React, { useEffect, useState } from "react";
    import { useParams } from "react-router-dom";

    const InfoCard = () => {
    const { id } = useParams();
    const [info, setInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchInfo = async () => {
        try {
        setLoading(true);
        const response = await fetch(
            `https://api.themoviedb.org/3/movie/${id}?api_key=3d820eab8fd533d2fd7e1514e86292ea&language=en-US`
        );
        if (!response.ok) {
            throw new Error("Failed to fetch movie information");
        }
        const data = await response.json();
        setInfo(data);
        } catch (err) {
        setError(err.message);
        } finally {
        setLoading(false);
        }
    };

    useEffect(() => {
        fetchInfo();
    }, [id]);

    if (loading) return <div className="flex justify-center items-center h-64">Loading...</div>;
    if (error) return <div className="text-red-500 text-center">Error: {error}</div>;
    if (!info) return <div className="text-center">No information available</div>;

    return (
        <div className="container mx-auto px-4 my-8 ">
        <div className="flex flex-col md:flex-row gap-8">
            <div className="md:w-1/3 lg:w-1/4">
            {info.poster_path ? (
                <img 
                src={`https://image.tmdb.org/t/p/w500${info.poster_path}`} 
                alt={info.title} 
                className="w-full rounded-lg shadow-lg mb-4" 
                />
            ) : (
                <div className="w-full h-96 bg-gray-200 flex items-center justify-center rounded-lg mb-4">
                <span className="text-gray-500">No image available</span>
                </div>
            )}
            </div>
            
            <div className="md:w-2/3 lg:w-3/4 md:mt-0 ">
            <h2 className="text-2xl md:text-3xl font-bold mb-4 lex flex-row justify-between bg-fuchsia-400 h-96 w-96">{info.title}</h2>
            
            <div className="space-y-3">
                <p className="text-gray-800">
                <span className="font-semibold">Overview:</span> {info.overview || "No overview available"}
                </p>
                
                <p>
                <span className="font-semibold">Release Date:</span> {info.release_date || "Unknown"}
                </p>
                
                <p>
                <span className="font-semibold">Rating:</span> {info.vote_average} / 10
                </p>
                
                <p>
                <span className="font-semibold">Genres:</span> {
                    info.genres && info.genres.length > 0 
                    ? info.genres.map(genre => genre.name).join(', ') 
                    : "Not specified"
                }
                </p>
                
                <p>
                <span className="font-semibold">Status:</span> {info.status || "Unknown"}
                </p>
                
                <p>
                <span className="font-semibold">Runtime:</span> {info.runtime ? `${info.runtime} minutes` : "Unknown"}
                </p>
            </div>
            </div>
        </div>
        </div>
    );
    };

    export default InfoCard;
