import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const InfoCard = () => {
  const { id } = useParams();
  const [info, setInfo] = useState(null);

  const fetchInfo = async () => {
    const response = await fetch(`
      https://api.themoviedb.org/3/movie/${id}?api_key=3d820eab8fd533d2fd7e1514e86292ea&language=en-US`);
    const data = await response.json();
    setInfo(data);
  };

  useEffect(() => {
    fetchInfo();
  }, [id]);

  if (!info) return <div className="text-center">Loading...</div>;

  return (
    <div className="container my-5 px-25">
      <div className="row">
        <div className="col-md-3">
          <img
            src={`https://image.tmdb.org/t/p/w500${info.poster_path}`}
            alt={info.title}
            className="img-fluid mb-4"
          />
        </div>
        <div className="col-md-6 ">
          <h2 className="mb-3 P6">{info.title}</h2>
          <p><strong>Overview:</strong> {info.overview}</p>
          <p><strong>Release Date:</strong> {info.release_date}</p>
          <p><strong>Rating:</strong> {info.vote_average} / 10</p>
          <p><strong>Genres:</strong> {info.genres.map(genre => genre.name).join(', ')}</p>
          <p><strong>Status:</strong> {info.status}</p>
          <p><strong>Runtime:</strong> {info.runtime} minutes</p>
        </div>
      </div>
    </div>
  );
};

export default InfoCard;
