import React, { useState, useEffect } from "react";
import { img_300, unavailable } from "../Components/config";
import { Link } from "react-router-dom";
import Pagination from "../Components/Pagination";

const Trending = () => {
  const [state, setState] = useState([]);
  const [page, setPage] = useState(1);

  const fetchTrending = async () => {
    const data = await fetch(
      `https://api.themoviedb.org/3/trending/all/day?api_key=3d820eab8fd533d2fd7e1514e86292ea&page=${page}`
    );
    const dataJ = await data.json();
    setState(dataJ.results);
  };

  useEffect(() => {
    fetchTrending();
  }, [page]);

  return (
    <div className="max-w-7xl mx-auto px-4 py-12">
      {/* Titre */}
      <div className="text-center mb-10">
        <h4 className="text-3xl font-bold underline text-white">Trending Today</h4>
      </div>

      {/* Grille des cartes */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {state.map((val) => {
          const {
            name,
            title,
            poster_path,
            first_air_date,
            release_date,
            media_type,
            id,
          } = val;

          return (
            <div key={id} className="bg-gray-900 rounded-lg shadow-lg overflow-hidden">
              <img
                src={poster_path ? `${img_300}/${poster_path}` : unavailable}
                alt={title || name}
                className="w-full h-80 object-cover"
              />
              <div className="p-4 text-white">
                <h5 className="text-lg font-semibold text-center mb-2">
                  {title || name}
                </h5>
                <div className="flex justify-between items-center text-sm text-gray-300">
                  <span>{media_type === "tv" ? "TV" : "Movie"}</span>
                  <span>{first_air_date || release_date}</span>
                  <Link
                    to="/Infocard"
                    className="text-blue-500 hover:underline"
                  >
                    Info
                  </Link>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Pagination */}
      <div className="mt-10">
        <Pagination page={page} setPage={setPage} />
      </div>
    </div>
  );
};

export default Trending;
