import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const InfoCard = () => {
  const { id } = useParams();
  const [info, setInfo] = useState(null);

  const fetchInfo = async () => {
    const data = await fetch(`
      https://api.themoviedb.org/3/tv/${id}?api_key=3d820eab8fd533d2fd7e1514e86292ea&language=en-US`);
    const dataJ = await data.json();
    setInfo(dataJ);
  };

  useEffect(() => {
    fetchInfo();
  }, [id]);

  if (!info) return <div>Loading...</div>;

  return (
    <div>
      <h1>{info.name}</h1>
      <p>{info.overview}</p>
      <img src={`https://image.tmdb.org/t/p/w300${info.poster_path}`} alt={info.name} />
      <p>First Air Date: {info.first_air_date}</p>
      {/* Ajoutez d'autres informations que vous souhaitez afficher */}
    </div>
  );
};

export default InfoCard;
