import React, { useState, useEffect } from "react";
import { img_300, unavailable } from "../Components/config";
import Pagination from "../Components/Pagination";
import Genre from "../Components/Genre";
import useGenre from "../useGenre";
import { Link } from "react-router-dom";

const Movies = () => {
  const [state, setState] = useState([]);
  const [page, setPage] = useState(1);
  const [genre, setGenre] = useState([]);
  const [value, setValue] = useState([]);
  const genreURL = useGenre(value);

  const fetchTrending = async () => {
    const data = await fetch(
      `https://api.themoviedb.org/3/discover/movie?api_key=3d820eab8fd533d2fd7e1514e86292ea&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=${page}&with_genres=${genreURL}`
    );
    const dataJ = await data.json();
    setState(dataJ.results);
  };

  useEffect(() => {
    fetchTrending();
  }, [page, genreURL]);

  return (
    <div className="max-w-7xl mx-auto px-4 py-10">
      <h1 className="text-4xl font-bold text-center underline text-white mb-8">
        Movies
      </h1>

      <Genre
        genre={genre}
        setGenre={setGenre}
        setPage={setPage}
        type="movie"
        value={value}
        setValue={setValue}
      />

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mt-8">
        {state.map((movie) => {
          const { title, poster_path, release_date, media_type, id } = movie;

          return (
            <div
              key={id}
              className="bg-gray-900 rounded-xl shadow-lg overflow-hidden transition transform hover:scale-105"
            >
              <img
                src={poster_path ? `${img_300}/${poster_path}` : unavailable}
                alt={title}
                className="w-full h-[400px] object-cover"
              />
              <div className="p-4 text-white">
                <h2 className="text-lg font-semibold text-center mb-2">
                  {title}
                </h2>
                <div className="flex justify-between text-sm text-gray-300 mb-3">
                  <span>{media_type === "tv" ? "TV Series" : "Movie"}</span>
                  <span>{release_date}</span>
                </div>
                <Link
                  to={`/Infocard/${id}`}
                  className="block text-center bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                >
                  Information
                </Link>
              </div>
            </div>
          );
        })}
      </div>

      <div className="mt-10">
        <Pagination page={page} setPage={setPage} />
      </div>
    </div>
  );
};

export default Movies;
