import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const InfoCard = () => {
  const { id } = useParams();
  const [info, setInfo] = useState(null);

  const fetchInfo = async () => {
    const response = await fetch(`
      https://api.themoviedb.org/3/movie/${id}?api_key=3d820eab8fd533d2fd7e1514e86292ea&language=en-US`);
    const data = await response.json();
    setInfo(data);
  };

  useEffect(() => {
    fetchInfo();
  }, [id]);

  if (!info) return <div className="text-center">Loading...</div>;

  return (
    <div className="max-w-7xl mx-auto my-5 px-4">
      <div className="flex flex-col md:flex-row gap-8">
        <div className="md:w-1/3 lg:w-1/4 flex-shrink-0">
          <img
            src={`https://image.tmdb.org/t/p/w500${info.poster_path}`}
            alt={info.title}
            className="rounded-lg shadow-lg w-full"
          />
        </div>
        <div className="flex-grow space-y-4">
          <h2 className="text-4xl font-bold text-gray-900">{info.title}</h2>
          <div className="prose prose-lg max-w-none">
            <p className="text-gray-700 leading-relaxed">{info.overview}</p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
              <div className="space-y-2">
                <p className="flex items-center">
                  <span className="font-semibold text-gray-700 w-32">Release Date:</span>
                  <span>{info.release_date}</span>
                </p>
                <p className="flex items-center">
                  <span className="font-semibold text-gray-700 w-32">Rating:</span>
                  <span>{info.vote_average} / 10</span>
                </p>
                <p className="flex items-center">
                  <span className="font-semibold text-gray-700 w-32">Runtime:</span>
                  <span>{info.runtime} minutes</span>
                </p>
              </div>
              <div className="space-y-2">
                <p className="flex items-center">
                  <span className="font-semibold text-gray-700 w-32">Status:</span>
                  <span>{info.status}</span>
                </p>
                <p className="flex items-start">
                  <span className="font-semibold text-gray-700 w-32">Genres:</span>
                  <span>{info.genres.map(genre => genre.name).join(', ')}</span>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InfoCard;
