import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { img_300, unavailable } from "../Components/config";

const Infocard = () => {
  const { id } = useParams(); // Get the ID from the URL
  const [data, setData] = useState(null);

  const fetchDetail = async () => {
    const response = await fetch(`
      https://api.themoviedb.org/3/movie/${id}?api_key=3d820eab8fd533d2fd7e1514e86292ea&language=en-US`);
    const detailData = await response.json();
    setData(detailData);
  };

  useEffect(() => {
    fetchDetail();
  }, [id]);

  if (!data) return <div>Loading...</div>;

  return (
    <div className="container">
      <div className="row py-5">
        <div className="col-12 text-center">
          <h1>{data.title || data.name}</h1>
          <img
            src={data.poster_path ? `${img_300}/${data.poster_path}` : unavailable}
            alt={data.title || data.name}
            className="img-fluid"
          />
          <p>{data.overview}</p>
          <p><strong>Release Date:</strong> {data.release_date || data.first_air_date}</p>
          <p><strong>Rating:</strong> {data.vote_average}</p>
        </div>
      </div>
    </div>
  );
};
export default Infocard;
