import React, { useState, useEffect } from "react";
import { img_300, unavailable } from "../Components/config";
import Pagination from "../Components/Pagination";
import Genre from "../Components/Genre";
import useGenre from "../useGenre";
import { Link } from "react-router-dom";

const Movies = () => {
  const [state, setState] = useState([]);
  const [page, setPage] = useState(1);
  const [genre, setGenre] = useState([]);
  const [value, setValue] = useState([]);
  const genreURL = useGenre(value);

  const fetchTrending = async () => {
    const data = await fetch(
      `https://api.themoviedb.org/3/discover/movie?api_key=3d820eab8fd533d2fd7e1514e86292ea&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=${page}&with_genres=${genreURL}`
    );
    const dataJ = await data.json();
    setState(dataJ.results);
  };

  useEffect(() => {
    fetchTrending();
  }, [page, genreURL]);

  return (
    <div className="max-w-7xl mx-auto px-4 py-10">
      <h1 className="text-4xl font-bold text-center underline mb-10">Movies</h1>

      {/* Genres */}
      <Genre
        genre={genre}
        setGenre={setGenre}
        setPage={setPage}
        type="movie"
        value={value}
        setValue={setValue}
      />

      {/* Movie Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8 mt-8">
        {state.map((movie) => {
          const { title, poster_path, release_date, media_type, id } = movie;
          return (
            <div key={id} className="bg-gray-900 rounded-lg shadow-lg overflow-hidden">
              <img
                src={poster_path ? `${img_300}/${poster_path}` : unavailable}
                alt={title}
                className="w-full h-80 object-cover"
              />
              <div className="p-4">
                <h5 className="text-lg font-semibold text-center text-white mb-2">{title}</h5>
                <div className="flex justify-between items-center text-gray-300 text-sm mb-4">
                  <span>{media_type === "tv" ? "TV Series" : "Movie"}</span>
                  <span>{release_date}</span>
                </div>
                <div className="text-center">
                  <Link
                    to={`/Infocard/${id}`}
                    className="inline-block bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded transition"
                  >
                    Information
                  </Link>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Pagination */}
      <div className="mt-10">
        <Pagination page={page} setPage={setPage} />
      </div>
    </div>
  );
};

export default Movies;
