import React, { useState, useEffect } from "react";
import { img_300, unavailable } from "../Components/config";
import Pagination from "../Components/Pagination";
import Genre from "../Components/Genre";
import useGenre from "../useGenre";
import { Link, useParams } from "react-router-dom";

const TV = () => {
  const { id } = useParams();
  const [state, setState] = useState([]);
  const [page, setPage] = useState(1);
  const [genre, setGenre] = useState([]);
  const [value, setValue] = useState([]);
  const genreURL = useGenre(value);

  const fetchTrending = async () => {
    const data = await fetch(
      `https://api.themoviedb.org/3/discover/tv?api_key=3d820eab8fd533d2fd7e1514e86292ea&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=${page}&with_genres=${genreURL}`
    );
    const dataJ = await data.json();
    setState(dataJ.results);
  };

  useEffect(() => {
    fetchTrending();
  }, [page, genreURL]);

  return (
    <div className="max-w-7xl mx-auto px-4 py-12 text-white">
      {/* Titre */}
      <div className="text-center text-3xl font-bold underline mb-10">
        TV Series
      </div>

      {/* Filtres */}
      <Genre
        genre={genre}
        setGenre={setGenre}
        setPage={setPage}
        type="tv"
        value={value}
        setValue={setValue}
      />

      {/* Grille des cartes */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8 mt-8">
        {state.map((val) => {
          const {
            name,
            title,
            poster_path,
            first_air_date,
            release_date,
            media_type,
            id,
          } = val;

          return (
            <div
              key={id}
              className="bg-gray-900 rounded-lg shadow-lg overflow-hidden"
            >
              <img
                src={poster_path ? `${img_300}/${poster_path}` : unavailable}
                alt={title || name}
                className="w-full h-80 object-cover"
              />
              <div className="p-4">
                <h5 className="text-lg font-semibold text-center mb-2">
                  {title || name}
                </h5>
                <div className="flex justify-between items-center text-sm text-gray-300">
                  <span>{media_type === "movie" ? "Movie" : "TV"}</span>
                  <span>{first_air_date || release_date}</span>
                  <Link
                    to={`/InfoCard/${id}`}
                    className="text-blue-500 hover:underline"
                  >
                    Info
                  </Link>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Pagination */}
      <div className="mt-10">
        <Pagination page={page} setPage={setPage} />
      </div>
    </div>
  );
};

export default TV;
